    private void jButtonDangXuatMousePressed(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButtonDangXuatMousePressed
        int input = JOptionPane.showConfirmDialog(null, "Đăng xuất?", "Xác nhận",
                JOptionPane.YES_NO_OPTION);
        if (input == 0) {
            setVisible(false);
            DangNhap dn = new DangNhap();
            dn.setVisible(true);
        }
    }//GEN-LAST:event_jButtonDangXuatMousePressed

